import React, {Component} from 'react'
import { Container, Row, Col, Card, CardImg, CardText, CardBody, CardLink,
    CardTitle, CardSubtitle } from 'reactstrap';
import { cpus } from 'os';

class Movies extends Component {

    componentDidMount = () => {
        console.log(this.props.movies);
        const moviesList = this.props.movies.length ? this.props.movies.map(movie => (
            <Col sm="3" key={movie.movie_id}>
                <Card>
                            <img width="100%" src={movie.image_url} alt={movie.movie_name} />
                        <CardBody>
                            <CardText className="text-truncate">{movie.movie_name}</CardText>
                            <CardLink className="text-truncate" href={movie.play_url}>{movie.platform.toUpperCase()}</CardLink>
                        </CardBody>
                </Card>
            </Col>

    )) : <p className="container center">Uh Oh!! We could not find the movie you entered. Please search a different title.</p>
    }

    render(){
        return (
            <Container>
                <Row>
                    {this.moviesList}
                </Row>  
            </Container>
        )
    }

}

export default Movies;
