import React from 'react';
import Search from './components/Search'
import Movies from './components/Movies'
import {Component} from 'react'

class App extends Component {
  state = {
    movies: [
      {"movie_id":"1558559215","platform":"hotstar","movie_name":" Deadpool 2","image_url":"https://img1.hotstarext.com/image/upload/f_auto,t_web_vl_3x/sources/r1/cms/prod/7413/297413-v","play_url":"https://www.hotstar.com/movies/deadpool-2/1770020234"},
      {"movie_id":"1558558490","platform":"hotstar","movie_name":" A Wrinkle In Time","image_url":"https://img1.hotstarext.com/image/upload/f_auto,t_web_vl_3x/sources/r1/cms/prod/2772/312772-v","play_url":"https://www.hotstar.com/movies/a-wrinkle-in-time/1660010675"},
      {"movie_id":"1558559216","platform":"hotstar","movie_name":" Deadpool 2","image_url":"https://img1.hotstarext.com/image/upload/f_auto,t_web_vl_3x/sources/r1/cms/prod/7413/297413-v","play_url":"https://www.hotstar.com/movies/deadpool-2/1770020234"}
    ],

    token : ''
  }

  getMovies = (data) => {
     this.setState({
       movies: data
     })
  }

  componentDidMount = () => {
      fetch('https://vo10eml1c4.execute-api.us-east-1.amazonaws.com/latest/api/get_token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({secret_key: 'QWERTYZXCVB'})
      })
      .then(response => response.json())
      .then(data => {
            fetch('https://vo10eml1c4.execute-api.us-east-1.amazonaws.com/latest/api/get_movies', {
            method: 'GET',
            headers: {
              Authorization : 'Bearer '+data.token,
            }
          })
          .then(response => response.json())
          .then(data => this.setState({movies:data.movies}))
      }) 
  }




  render(){
    return (
      <div className="container App">
        <Search getMovies={this.getMovies}/>
        <Movies movies={this.state.movies}/>
      </div>
    )
  }
}

export default App;
