import React from 'react'
import {Component} from 'react'

class Search extends Component {
    state = {
        search: '',
        fetch: true
    }


    handleChange = (e) => {
        this.setState({
            search: e.target.value
        });

        console.log('Inside Handle Change : '+this.state.search);

    }

    handleSubmit = (e) => {
        e.preventDefault();
        const search = this.state.search;
        search.length ?
        (
            fetch('https://vo10eml1c4.execute-api.us-east-1.amazonaws.com/latest/api/get_token', {
                method: 'POST',
                headers: {
                'Content-Type': 'application/json'
                },
                body: JSON.stringify({secret_key: 'QWERTYZXCVB'})
            })
            .then(response => response.json())
            .then(data => {
                    console.log('Before calling get movie : '+search);
                    fetch('https://vo10eml1c4.execute-api.us-east-1.amazonaws.com/latest/api/get_movie?movie_name='+search, {
                    method: 'GET',
                    headers: {
                    Authorization : 'Bearer '+data.token,
                    }
                })
                .then(response => response.json())
                .then(data => this.props.getMovies(data.movies))
            })
      )
      : console.log('No Value Entered');

      this.setState({
          search : ''
      })
    }

    render(){
        return(
            <div className="container center">
                <h1 className="center blue-text"> Search & Chill</h1>
                <form className="col s12" onSubmit={this.handleSubmit}>
                    <div className="row">
                        <div className="input-field col s6">
                                <input id="input_text" type="text" onChange={this.handleChange} value={this.state.search}/>
                                <label htmlFor="input_text" className="active">Movie Name</label>
                        </div>
                     </div>
                </form>
            </div>

        )
    }

}

export default Search;
