import React from 'react'

const Movies = (props) => {

    const titleStyle = {
        width:'100%', 
        background: rgba(0, 0, 0, 0.5)
    }




    const moviesList = props.movies.length ? props.movies.map(movie => (
        <div className="row" key={movie.movie_id}>
            <div className="col m4">
                <div className="card">
                <div clclassNameass="card-image">
                    <img src={movie.image_url}/>
                    <span className="card-title" style={titleStyle}>{movie.movie_name}</span>
                </div>
                <div className="card-action">
                    <a href={movie.play_url}>{movie.platform}</a>
                </div>
                </div>
            </div>
        </div>
    )) : <p className="center">Please search a Movie!!</p>

    return (
        <div className="movies">
           {moviesList}
        </div>
    )

}

export default Movies;
