import React, {Component} from 'react'
import { Container, Row, Col, Card, CardImg, CardText, CardBody, CardLink,
    CardTitle, CardSubtitle, Tooltip } from 'reactstrap';

class Movies extends Component{

    state = {
        tooltipOpen: false,
    }

    toggle = () => {
        this.setState({
          tooltipOpen: !this.state.tooltipOpen
        });
      }

    moviesList = this.props.movies.length ? this.props.movies.map(movie => (
             <Col sm="6" md="4" lg="3" xs="12">
                <Card key={movie.movie_id}> 
                            <img width="100%" height="200px" src={movie.platforms[0].image_url} alt={movie.movie_name} />
                        <CardBody>
                            <CardText className="text-truncate">{movie.movie_name}</CardText>
                            {
                                movie.platforms.map(platform => (
                                    platform.platform_name.toUpperCase()==="PRIME_VIDEO"?
                                    (
                                        <span>
                                            <CardLink id={movie_id+"_"+platform_name} className="text-truncate" href={platform.play_url} target="_blank">PRIME</CardLink>
                                            <Tooltip placement="bottom" isOpen={this.state.tooltipOpen} target={movie_id+"_"+platform_name} toggle={this.toggle}></Tooltip>
                                        </span>
                                    ):
                                    <CardLink className="text-truncate" href={platform.play_url} target="_blank">{platform.platform_name.toUpperCase()}</CardLink>
                                ))
                            } 
                        </CardBody>
                </Card>
            </Col>

    )) : <p className="center"></p>

    render() {
        return (
            <Container>
                <Row>
                    {moviesList}
                </Row>  
            </Container>
        )
    }

}

export default Movies;
