import React from 'react'
import { Container, Row, Col, Card, CardImg, CardText, CardBody, CardLink,
    CardTitle, CardSubtitle } from 'reactstrap';
import { cpus } from 'os';

const Movies = (props) => {

    const moviesList = props.movies.length ? props.movies.map(movie => (
             <Col sm="4">
                <Card key={movie.movie_id}>
                    <CardBody>
                        <CardTitle className="text-truncate">{movie.movie_name}</CardTitle>
                        </CardBody>
                            <img width="100%" src={movie.image_url} alt={movie.movie_name} />
                        <CardBody>
                        <CardLink href={movie.play_url}>{movie.platform}</CardLink>
                    </CardBody>
                </Card>
            </Col>

    )) : <p className="center">Please search a Movie!!</p>

    return (
        <Container>
            <Row>
                {moviesList}
            </Row>  
        </Container>
    )

}

export default Movies;
