import React from 'react'
import { Card, CardImg, CardText, CardBody, CardLink,
    CardTitle, CardSubtitle } from 'reactstrap';

const Movies = (props) => {

    const moviesList = props.movies.length ? props.movies.map(movie => (
            <Card key={movie.movie_id}>
                <CardBody>
                    <CardTitle>{movie.movie_name}</CardTitle>
                    </CardBody>
                        <img width="100%" src={movie.image_url} alt={movie.movie_name} />
                    <CardBody>
                    <CardLink href={movie.play_url}>{movie.platform}</CardLink>
                </CardBody>
            </Card>


    )) : <p className="center">Please search a Movie!!</p>

    return (
        <div>

            {moviesList}

        </div>
    )

}

export default Movies;
