import React from 'react'
import { Container, Row, Col, Card, CardImg, CardText, CardBody, CardLink,
    CardTitle, CardSubtitle, OverlayTrigger, Tooltip } from 'reactstrap';

const Movies = (props) => {

    const moviesList = props.movies.length ? props.movies.map(movie => (
             <Col sm="6" md="4" lg="3" xs="12">
                <Card key={movie.movie_id}> 
                            <img width="100%" height="200px" src={movie.platforms[0].image_url} alt={movie.movie_name} />
                        <CardBody>
                            <CardText className="text-truncate">{movie.movie_name}</CardText>
                            {
                                movie.platforms.map(platform => (

                                    platform.platform_name.toUpperCase()==="PRIME_VIDEO"?
                                    (
                                        <span>
                                            <Tooltip id={platform.play_url}>{platform.is_paid?"Paid":"Free"}</Tooltip>
                                            <OverlayTrigger trigger="hover" placement="right" overlay={platform.play_url}>
                                                <CardLink className="text-truncate" href={platform.play_url} target="_blank">PRIME</CardLink>
                                            </OverlayTrigger>
                                        </span>
                                    ):
                                    <CardLink className="text-truncate" href={platform.play_url} target="_blank">{platform.platform_name.toUpperCase()}</CardLink>
                                ))
                            } 
                        </CardBody>
                </Card>
            </Col>

    )) : <p className="center"></p>

    return (
        <Container>
            <Row>
                {moviesList}
            </Row>  
        </Container>
    )

}

export default Movies;
