import React from 'react'

const Movies = (props) => {

    const imgStyle = {
        width: '50px',
        height: '50px'
    } 

    const moviesList = props.movies.length ? props.movies.map(movie => (
        <div key={movie.movie_id}>
            <a href={movie.play_url} target="_blank"><img src={movie.image_url} alt={movie.movie_name}/></a>

            <div class="card small">
                <div class="card-image waves-effect waves-block waves-light">
                    <img class="activator" src={movie.image_url} style={imgStyle}/>
                </div>
                <div class="card-content">
                <span class="card-title activator grey-text text-darken-4">{movie.movie_name}</span>
                <p><a href={movie.play_url}>{movie.platform}</a></p>
                </div>
                <div class="card-reveal">
                <span class="card-title grey-text text-darken-4">Card Title<i class="material-icons right">close</i></span>
                </div>
            </div>

        </div>
    )) : <p className="center">Please search a Movie!!</p>

    return (
        <div>
           {moviesList}
        </div>
    )

}

export default Movies;
