import React from 'react';
import Search from './components/Search'
import Movies from './components/Movies'
import {Component} from 'react'
import { Spinner } from 'reactstrap';

class App extends Component {
  state = {
    movies: [],
    isFetchComplete: true
  }

  setIsFetchComplete = (value) => {
    value!==this.state.isFetchComplete? this.setState({isFetchComplete: value}) : console.log(value)
  }

  getMovies = (data) => {
     this.setState({
       movies: data
     })
  }

  render(){
    return (
      <div className="container App">
        <Search getMovies={this.getMovies}/>
        {this.state.isfetchComplete ? <Movies movies={this.state.movies}/> : <Spinner className="container center" style={{ width: '3rem', height: '3rem' }} type="grow" /> }
      </div>
    )
  }
}

export default App;
